e\n        152173,                -- Serenity\n        -- Paladin\n        152262,                -- Seraphim\n        343527,                -- Execution Sentence\n        31884,                -- Avenging Wrath\n        -- Rogue\n        185313,                -- Shadow Dance\n        13750,                -- Adrenaline Rush\n        -- Demon Hunter\n        191427,                -- Metamorphosis\n        -- Warrior\n        107574,                -- Avatar\n    },\n    IsDuelPvP = {\n        190319,                -- Combustion\n    },\n    IsVeryDangerousPvP = {\n        337433,             -- Convoke\n        323764,                -- Convoke\n    },\n}\nlocal ReflectSpellsPvP                         = {}\ndo\n    for _, v in ipairs({Temp.IsSpellReflectPvPCC, Temp.IsSpellReflectPvPDmg, Temp.IsBreakableSpellReflectPvPCC}) do\n        for spell, spellValue in pairs(v) do\n            if type(spellValue) == \"table\" then\n                ReflectSpellsPvP[spell] = CopyTable(spellValue)\n            else\n                ReflectSpellsPvP[spell] = spellValue\n            end\n        end\n    end\nend\n\nfunction A.ShouldSpellReflect(self, delay)\n    local SpellReflection    = A[A.PlayerSpec].SpellReflection\n    local BerserkerRage        = A[A.PlayerSpec].BerserkerRage\n\n    -- (1)\n    if not SpellReflection or not BerserkerRage or not SpellReflection:IsReadyByPassCastGCD(player) then return end\n\n    -- (2)\n    if A.Zone ~= \"arena\" then\n        local castLeft, _, spellID\n        local latency = GetLatency() + (delay * 2)\n\n        for Reflect_UnitID in pairs(Reflect_Nameplates) do\n            castLeft, _, spellID = Unit(Reflect_UnitID):IsCastingRemains()\n\n            if castLeft > 0 and castLeft <= latency and UnitIsUnit(unitIDtargets[Reflect_UnitID], player) and\n            (\n                -- (2.a)\n                (A.IsInPvP and ReflectSpellsPvP[spellID] and (not Temp.IsBreakableSpellReflectPvPCC[spellID] or BerserkerRage:GetCooldown() > 0)) or\n                -- (2.b)\n                (not A.IsInPvP and Temp.IsSpellReflectPvE[spellID])\n            ) then\n                return true\n            end\n        end\n    -- (3)\n    else\n        local castLeft, _, spellID\n        local latency = delay + GetLatency() --+ delay\n\n        for i = 1, 3 do\n            castLeft, _, spellID = Unit(arenaIDs[i]):IsCastingRemains()\n            -- (3.a)\n            if castLeft > 0 and castLeft <= latency and UnitIsUnit(unitIDtargets[arenaIDs[i]], player) and ReflectSpellsPvP[spellID] and (not Temp.IsBreakableSpellReflectPvPCC[spellID] or BerserkerRage:GetCooldown() > 0) then\n                return true\n            end\n        end\n    end\nend\n\nfunction A.IntervenePvP(partyUnitID, delay, hasPriest)\n    local Intervene = A[A.PlayerSpec].Intervene\n\n    if A.IsInPvP and A.Zone == \"arena\" and Intervene then\n        local castLeft, _, spellID\n        local latency = GetLatency() + (delay * 2)\n        for i = 1, 3 do\n            castLeft, _, spellID = Unit(arenaIDs[i]):IsCastingRemains()\n            if castLeft > 0 and castLeft <= latency and UnitIsUnit(unitIDtargets[arenaIDs[i]], partyUnitID) and (ReflectSpellsPvP[spellID] and not hasPriest or spellID == 323673) then\n                return true\n            end\n        end\n    end\nend\n\nlocal DisarmPvPunits     = setmetatable({}, { __index = function(t, v)\n    t[v] = GetToggle(2, \"DisarmPvPunits\")\n    return t[v]\nend})\nlocal ImunBuffsCC                              = {\"CCTotalImun\", \"DamagePhysImun\", \"TotalImun\"}\nlocal ImunBuffsInterrupt                     = {\"KickImun\", \"TotalImun\", \"DamagePhysImun\"}\n\n\n\nfunction A.ShouldDisarm(unitID, target, player)\n    local Disarm = A[A.PlayerSpec].Disarm\n    if  A.IsInPvP and -- Is in action PvP mode\n        Disarm and -- I have disarm\n        GetToggle(2, \"DisarmPvP\") ~= \"OFF\" and -- Disarm is not toggled off in action\n        Disarm:IsReady(unitID) and -- Disarm is ready to use\n        Unit(unitID):IsPlayer() and -- Target is a player\n        Disarm:AbsentImun(unitID, ImunBuffsCC, true) and -- Target has no immunities on\n        Unit(unitID):IsControlAble(\"disarm\") and -- Target can be disarmed\n        Unit(unitID):InCC() == 0 and -- Target is not CCd\n        Unit(unitID):HasDeBuffs(\"Disarmed\") == 0 and -- Target is not already disarmed\n        Unit(unitID):HasBuffs(Temp.IsDisarmPvP) > 4 and not -- Has one of the buffs in our ID list for > 4 seconds\n        Unit(unitID):IsHealer() then -- Is not a healer\n        return true\n    end\nend\nA.ShouldDisarm = A.MakeFunctionCachedDynamic(A.ShouldDisarm)\n\nfunction A.ShouldDuel(unitID)\n    local Duel = A[A.PlayerSpec].Duel\n    if A.IsInPvP and -- Is in action PvP mode\n        Duel and -- I specced duel\n        Duel:IsReady(unitID) and -- Duel is off CD\n        Unit(unitID):IsPlayer() and -- Target is a player\n        Unit(unitID):InCC() == 0 and -- Target is not CCd\n        Unit(unitID):HasBuffs(Temp.IsDuelPvP) > 2 and -- Has one of the buffs in our ID list for > 3 seconds\n        Unit(unitID):HasDeBuffs(\"Stuned\") == 0 and not --  Not stunned\n        Unit(unitID):IsHealer() and not --Is not a healer\n        UnitIsUnit(unitID .. \"target\", \"player\") then -- Not targetting me\n        return true\n    end\nend\nA.ShouldDuel = A.MakeFunctionCachedDynamic(A.ShouldDuel)\n\nfunction A:CanInterruptPassive(unitID, countGCD)\n    if A.IsInPvP and (A.Zone == \"arena\" or A.Zone == \"pvp\") then\n        if self.isPummel then\n            local useKick, _, _, notInterruptable = InterruptIsValid(unitID, \"Heal\", nil, countGCD)\n            if not useKick then\n                useKick, _, _, notInterruptable = InterruptIsValid(unitID, \"PvP\", nil, countGCD)\n            end\n            if useKick and not notInterruptable and self:IsReadyByPassCastGCD(unitID) and self:AbsentImun(unitID, ImunBuffsInterrupt, true) then\n                return true\n            end\n        end\n\n        if self.isStormBolt then\n            local StormBoltPvP = GetToggle(2, \"StormBoltPvP\")\n            if StormBoltPvP and StormBoltPvP ~= \"OFF\" and self:IsReadyByPassCastGCD(unitID) then\n                local _, useCC, castRemainsTime\n                if Toggle == \"BOTH\" then\n                    useCC, _, _, castRemainsTime = select(2, InterruptIsValid(unitID, \"Heal\", nil, countGCD))\n                    if not useCC then\n                        useCC, _, _, castRemainsTime = select(2, InterruptIsValid(unitID, \"PvP\", nil, countGCD))\n                    end\n                else\n                    useCC, _, _, castRemainsTime = select(2, InterruptIsValid(unitID, Toggle, nil, countGCD))\n                end\n                if useCC and castRemainsTime >= GetLatency() and Unit(unitID):IsControlAble(\"stun\") and not Unit(unitID):InLOS() and self:AbsentImun(unitID, ImunBuffsCC, true) then\n                    return true\n                end\n            end\n        end\n    end\nend",
